<template>
  <div class="login-container">
    <!-- ref 相当于 id，:model 表单数据对象, label-width 表单域标签的的宽度 -->
    <el-form
      ref="form"
      :model="form"
      :rules="rules"
      label-width="0"
      class="login-form"
    >
      <h2 class="login-title">内部项目交流平台</h2>
      <el-form-item prop="username">
        <el-input
          prefix-icon="el-icon-user"
          v-model="form.username"
          placeholder="请输入帐号"
        ></el-input>
      </el-form-item>
      <el-form-item prop="password">
        <el-input
          prefix-icon="el-icon-lock"
          type="password"
          v-model="form.password"
          placeholder="请输入密码"
        ></el-input>
      </el-form-item>
      <el-form-item prop="captcha">
        <el-row :gutter="20">
          <el-col :span="12">
            <el-input v-model="form.captcha" placeholder="验证码"></el-input>
          </el-col>
          <el-col :span="12" style="height: 40px">
            <img
              class="yzm-img"
              @click="getCaptcha"
              :src="yzmImg"
              fallback="data:image/png;base64,"
            />
          </el-col>
        </el-row>
      </el-form-item>
      <el-form-item>
        <el-button
          type="primary"
          @click="submitForm('form')"
          style="width: 100%"
          >登 录</el-button
        >
      </el-form-item>
    </el-form>
  </div>
</template>
<script>
export default {
  data() {
    return {
      form: {
        username: "",
        password: "",
        captcha: "",
      },
      rules: {
        username: [
          { required: true, message: "请输入用户名", trigger: "blur" },
        ],
        password: [{ required: true, message: "请输入密码", trigger: "blur" }],
        captcha: [{ required: true, message: "请输入验证码", trigger: "blur" }],
      },
      uuid: "",
      yzmImg: "",
    };
  },
  created() {
    this.getCaptcha();
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.$store
            .dispatch("Login", { ...this.form, uuid: this.uuid })
            .then((response) => {
              if (response.code == 200) {
                // 获取用户详情
                this.$store
                  .dispatch("GetUserInfo")
                  .then((response) => {
                    if (response.code == 200) {
                      this.$router.push("/");
                    }
                  })
                  .catch((error) => {
                    console.log(error);
                  });
              } else {
                this.getCaptcha();
              }
            });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    },
    buildUUID() {
      const hexList = [];
      for (let i = 0; i <= 15; i++) {
        hexList[i] = i.toString(16);
      }
      let uuid = "";
      for (let i = 1; i <= 36; i++) {
        if (i === 9 || i === 14 || i === 19 || i === 24) {
          uuid += "-";
        } else if (i === 15) {
          uuid += 4;
        } else if (i === 20) {
          uuid += hexList[(Math.random() * 4) | 8];
        } else {
          uuid += hexList[(Math.random() * 16) | 0];
        }
      }
      return uuid.replace(/-/g, "");
    },
    getCaptcha() {
      this.uuid = this.buildUUID();
      this.yzmImg = `${process.env.VUE_APP_SERVICE_URL}/captcha.jpg?uuid=${this.uuid}`;
    },
  },
};
</script>
<style lang="scss">
@import "../../assets/style/login.scss";
</style>
